import React, { useContext } from "react";
import { FlatList, StyleSheet, View, TouchableOpacity, Text, Alert } from "react-native";
import { useTheme } from "@react-navigation/native";
import { modalDemos } from "@/components/homeData";
import { DemoCard } from "@/components/DemoCard";
import { ThemeContext } from "@/contexts/ThemeContext"; // Assuming this is the correct path
import { MaterialIcons } from '@expo/vector-icons'; // Import MaterialIcons

const themes = [
  { name: "Default", value: "default" },
  { name: "Game Mode", value: "gameMode" },
  { name: "Dark", value: "dark" },
  { name: "Light", value: "light" },
  { name: "Theme 5", value: "theme5" },
  { name: "Theme 6", value: "theme6" },
  { name: "Theme 7", value: "theme7" },
  { name: "Theme 8", value: "theme8" },
  { name: "Theme 9", value: "theme9" },
  { name: "Theme 10", value: "theme10" },
];

export default function HomeScreen() {
  const theme = useTheme();
  const { setTheme } = useContext(ThemeContext);

  const handleThemeSelection = () => {
    Alert.alert(
      "Select Theme",
      "Are you sure you want to switch theme?",
      themes.map(themeOption => ({
        text: themeOption.name,
        onPress: () => {
          Alert.alert(
            "Confirm Theme Change",
            `Are you sure you want to switch to ${themeOption.name} theme?`,
            [
              { text: "Cancel", style: "cancel" },
              {
                text: "Yes",
                onPress: () => setTheme(themeOption.value),
              },
            ]
          );
        },
      }))
    );
  };

  return (
    <View style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <TouchableOpacity style={styles.menuButton} onPress={handleThemeSelection}>
        <MaterialIcons name="menu" size={30} color={theme.colors.text} />
      </TouchableOpacity>
      <FlatList
        data={modalDemos}
        renderItem={({ item }) => <DemoCard item={item} />}
        keyExtractor={(item) => item.route}
        contentContainerStyle={styles.listContainer}
        contentInsetAdjustmentBehavior="automatic"
        showsVerticalScrollIndicator={false}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  listContainer: {
    paddingTop: 48,
    paddingHorizontal: 16,
    paddingBottom: 100, // Extra padding for floating tab bar
  },
  menuButton: {
    position: "absolute",
    top: 10,
    left: 10,
    padding: 10,
    zIndex: 1,
  },
});
```

